link.script:
window.onload = function () {
    if ( ('ontouchstart' in window) || window.DocumentTouch && document instanceof DocumentTouch ) {
        document.documentElement.className += ' ' + 'touch';
    } else {
        document.documentElement.className += ' ' + 'no-touch';
    }
};

function checkForAdditionalMetrics(action) {
    switch (action) {
        case 'R2SEmailSuccess':
            return {
                key: 'metric' + 4,
                value: 1
            };
        case 'R2SBidSuccess':
            return {
                key: 'metric' + 5,
                value: 1
            };
        case 'R2SPhoneBegin':
            return {
                key: 'metric' + 7,
                value: 1
            };
        case 'R2SSMSBegin':
            return {
                key: 'metric' + 8,
                value: 1
            };
        case 'R2SExternalBegin':
            return {
                key: 'metric' + 9,
                value: 1
            };
        case 'PostAdPaidSuccess':
            return {
                key: 'metric' + 2,
                value: 1
            };
        case 'FeatureAdSuccess':
            return {
                key: 'metric' + 10,
                value: 1
            };
        case 'PostAdFreeSuccess':
            return {
                key: 'metric' + 11,
                value: 1
            };
        default:
            break;
    }
}

function trackPageEvents(events) {
    events.forEach(function(event) {
        if (event) {
            var eventData = event.split(',');
            var action = eventData[1];
            var label = eventData[2];
            var customMetric = checkForAdditionalMetrics(action);
            var gaEvent = {
                eventAction: action,
                eventLabel: label
            };

            if (customMetric) {
                gaEvent[ customMetric.key ] = customMetric.value;
            }

            window.ecGaEventsOnLoad.push(gaEvent);
        }
    });
}

window.ecGaEventsOnLoad = window.ecGaEventsOnLoad || [];
var attributeValue = '';
var pageEvents = attributeValue.split('&');
trackPageEvents(pageEvents);

window.sitespeedBootstrap = {
    loaded: false
};

function metricsHandler() {
    if (window.sitespeedBootstrap.loaded) {
        AURORA.Metrics.collect();
    } else {
        setTimeout(metricsHandler, 0);
    }
}

if (window.addEventListener) {
    window.addEventListener('load', metricsHandler, false);
} else if(window.attachEvent) {
    window.attachEvent('onload', metricsHandler);
}
